const INPUT: &'static [&'static str] = &[
r#"ULL
RRDDD
LURDL
UUUUD"#,
r#"



"#,
];

const INDEX: usize = 1;

const KEYPAD: [[char; 5]; 5] = [
    [' ', ' ', '1', ' ', ' '],
    [' ', '2', '3', '4', ' '],
    ['5', '6', '7', '8', '9'],
    [' ', 'A', 'B', 'C', ' '],
    [' ', ' ', 'D', ' ', ' '],
];

pub fn main() {
    let mut pos = (0, 2);
    for line in INPUT[INDEX].split("\n") {
        get_next_button_part2(&mut pos, line);
        print!("{}", KEYPAD[pos.1][pos.0]);
    }
    println!();
}

fn get_next_button_part2(pos: &mut (usize, usize), line: &str) {
    for dir in line.chars() {
        let new_pos;
        match (dir, pos.0, pos.1) {
            ('U', _, 0) | ('R', 4, _) | ('D', _, 4) | ('L', 0, _) => continue,
            (_, x, y) => {
                new_pos = match dir {
                    'U' => (x, y - 1),
                    'R' => (x + 1, y),
                    'D' => (x, y + 1),
                    'L' => (x - 1, y),
                    _ => panic!("Invalid direction: {:?}", dir),
                };
            }
        }
        if KEYPAD[new_pos.1][new_pos.0] == ' ' {
            continue;
        } else {
            *pos = new_pos;
        }
    }
}
